import React from 'react';
import Base from '../core/Base'
import { Link } from 'react-router-dom';

const ManageCategories = () => {

  const {user,token} = isAuthenticated();

    const [values,setValues] = useState({
        name:"",
        description:"",
        price:"",
        stock:"",
        photo:"",
        categories:[],
        category:"",
        loading:false,
        error:"",
        updatedProduct:"",
        getaRedirect:false,
        formData:""

    })

    const {name,description,price,stock,categories,category,loading,error,updatedProduct,getaRedirect,formData} = values;

    const preLoad = productId =>{
        getProduct(productId).then(data =>{

            // console.log(data);
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                preLoadCategories();
                setValues({
                    ...values,
                    name:data.name,
                    description:data.description,
                    price:data.price,
                    category:data.category._id,
                    stock:data.stock,
                    formData:new FormData()
                });

            }
        })
    }


const preLoadCategories = ()=>{
    getAllCategories().then(data=>{
        if(data.error){
            setValues({...values,error:data.error})
        }
        else{
            setValues({
                categories:data,formData:new FormData()
            })
        }
    })
}

const match = useMatch(
    "/admin/product/update/:productId"
  );


    useEffect(() => {
        preLoad(match.params.productId)
    }, []);

    const handleChange =name=> event =>{
        const value = name ==="photo" ? event.target.files[0] : event.target.value;
        formData.set(name,value);
        setValues({...values,[name]:value})

    }
    const onSubmit = event =>{
        event.preventDefault();
        setValues({...values,error:"",loading:true})
        updateProduct(match.params.productId,user._id,token,formData)
        .then(data =>{
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                setValues({...values,name:"",price:"",stock:"",description:"",photo:"",loading:false,updatedProduct:data.name})
            }
        })

}

    const successMessage =() =>(
        <div className=" alert alert-success mt-3" style={{display : updatedProduct ? "" : "none"}}>
            <h4>{updatedProduct} created Successfully</h4>
        </div>
    )

    const errorMessage =() =>(
        <div className=" alert alert-danger mt-3" style={{display : error ? "" : "none"}}>
            <h4>Not able to create {updatedProduct}</h4>
        </div>
    )

    const loadingMessage =()=>{
        return(
        loading && (
        <div className=" issue alert alert-info" style={{display: loading ? "" :"none"}}>
        Loading......
        </div>)
    )}

    const performRedirect =()=>{
        if(getaRedirect){
            if(user && user.role===1){
            return <Navigate to="/admin/dashboard" />
            }else{
            return <Navigate to="/user/dashboard" />
            }
        }

    }


  return (
    <Base title="Welcome admin" description="Manage products here">
    <h2 className="mb-4">All products:</h2>
    <Link className="btn btn-info" to={`/admin/dashboard`}>
      <span className="">Admin Home</span>
    </Link>
    <div className="row">
      <div className="col-12">
        <h2 className="text-center text-white my-3">Total 3 products</h2>

        <div className="row text-center mb-2 ">
          <div className="col-4">
            <h3 className="text-white text-left">I write code</h3>
          </div>
          <div className="col-4">
            <Link
              className="btn btn-success"
              to={`/admin/product/update/productId`}
            >
              <span className="">Update</span>
            </Link>
          </div>
          <div className="col-4">
            <button onClick={() => {}} className="btn btn-danger">
              Delete
            </button>
          </div>
        </div>
      </div>
    </div>
  </Base>
  )
}

export default ManageCategories
