import React,{useState,useEffect} from 'react';
import '../styles.css';
import Base from './Base';
import ProductCard from './Card';
import { loadCart } from './helper/cartHelper';

// import { getProducts } from './helper/coreapicalls';


const Cart =() => {


    const [products, setProducts] = useState([]);
    const [reload, setReload] = useState(false)

    useEffect(() => {
        setProducts(loadCart())
    }, [reload]);

    const loadAllProducts = () =>{
        return(
            <div >
                <h2 className='text-dark  text-center p-4'>Your products in Cart</h2>
                <div className=" col col-lg-4 col-md-4 d-flex align-items-stretch px-5 mb-4" >

                {products.map((product,index)=>{
                    return(
                    <ProductCard 
                    key={index}
                    product={product} 
                    removeFromCart={true} 
                    addtoCart={false}
                    setReload={setReload}
                    reload={reload}
                    />
                )})}
              </div> 


            </div>
        )
    }

    const loadCheckOut = () =>{
        return(
        <div >
                <h2 className='text-white'>CheckOut Here</h2>


            </div>
    )}



  return (
    <Base title="Cart Page" description="Ready to CheckOut" className='bg-dark'>
      <div className="row text-center">
      <h2  className="text-center mb-2 py-4 text-white ">Shopping Cart</h2>
        <div className='row'>
            <div className='col-6 shadow-lg bg-white '>{loadAllProducts()}</div>
            <div className='col-6'>{loadCheckOut()}</div>

        </div>
        </div>

    </Base>
  );
}

export default Cart;
