import React,{useState,useEffect} from 'react';
import { isAuthenticated } from '../auth/helper';
import { loadCart,cartEmpty } from './helper/cartHelper';
import { Link } from 'react-router-dom';

const StripeCheckout = (products,setReload=f=>f,reload=undefined) => {

    const [data, setData] = useState({
        loading:false,
        error:false,
        success:false,
        address:""

    })

    const token = isAuthenticated() && isAuthenticated().token
    const userId = isAuthenticated() && isAuthenticated().user._id

    const getFinalPrice =()=>{

        let amount=0 
        products.map(p=>{
            amount=amount+p.price;
        })
        return amount;
    }

    const showStripeButton = () =>{
        return isAuthenticated() ? (
            <button  className="shadow-md rounded ml-3  px-3" variant="primary">Pay with Stripe</button>
        ):(
            <Link to="/signin"><button className="shadow-md rounded ml-3  px-3" variant="warning">Add to Cart</button></Link>
        )
    }


  return (
    <h2 className='text-dark'>StripeCheckout loaded {getFinalPrice()}</h2>
  )
}

export default StripeCheckout;
