import React, {useState,useEffect} from 'react'
import Base from "../core/Base";
import { isAuthenticated } from '../auth/helper';
import { Link,Navigate } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {updateCategory,getCategory} from './helper/adminapicall'
import { useMatch } from 'react-router-dom';



const UpdateCategory = () => {

    // const [name,setName] = useState("");
    // const [error,setError] = useState(false);
    // const [success,setSuccess] = useState(false);

    const {user,token} = isAuthenticated();


    const [values,setValues] = useState({
        name:"",
        loading:false,
        error:"",
        updatedCategory:"",
        getaRedirect:"",
        formData:""

    })

    const {name,loading,error,updatedCategory,getaRedirect,formData} = values


    const preLoad = categoryId =>{
        getCategory(categoryId).then(data =>{

             console.log(data);
            if(data.error){
                console.log(error.response)
                setValues({...values,error:data.error})
            }else{
                // preLoadCategories();
                // setName({name:data.name});
                // setName(data.name);
                // console.log(data.name)
                setValues({
                    ...values,
                    name:data.name,
                    formData:new FormData()
                });
            }
        })
    }

    //  const preLoadCategories = ()=>{
    //     getAllCategories().then(data=>{
    //         if(data.error){
    //             console.log(data.error)
    //         }
    //         else{
    //             setName({name:data.name});
    //         }
    //     })
    // }

    const match = useMatch(
        "/admin/category/update/:categoryId"
      );


        useEffect(() => {
            preLoad(match.params.categoryId)
        },[]);


    const handleChange =event=>{
        // setValues({...values,error:""})
        // setError("")
        // setName(event.target.value)
        // console.log(event.target.value)

        const value = name ==="photo" ? event.target.files[0] : event.target.value;
        formData.set(name,value);
        setValues({...values,[name]:value})
    }

    const onSubmit =event=>{
        event.preventDefault();
        // setValues({...values,loading:true})
        // setError("");
        //  setSuccess(false);
        setValues({...values,error:"",loading:true})

        //backend request fired
        updateCategory(match.params.categoryId,user._id,token,
            {name}

            ).then(data =>{
            if(data.error){
                console.log(data.error)
                // setValues({...values,error:data.error})
            }else{
                setError("");
                setSuccess(true);
                // setName(event.target.value)
                // setName({name:event.target.value})
                // console.log(data)
                setValues({...values,name:"",loading:false,updatedCategory:data.name})
            }
        })
    }

    const successMessage = () =>{
        <div className=" alert alert-success mt-3" style={{display : updatedCategory ? "" : "none"}}>
            <h4>{updatedCategory} updated Successfully</h4>
        </div>
        // if(success){
        //     return <button className='m-2 p-2 shadow-lg rounded text-center btn-success text-success'>Category updated successfully </button>
        // }
    }

    const errorMessage = () =>{
        <div className=" alert alert-danger mt-3" style={{display : error ? "" : "none"}}>
            <h4>Not able to update {updatedCategory}</h4>
        </div>
        // if(error){
        //     return <button className='m-2 p-2 shadow-lg rounded text-center btn-danger text-white'>Failed to Update Category </button>
        // }
    }
    const loadingMessage =()=>{
        return(
        loading && (
        <div className=" issue alert alert-info" style={{display: loading ? "" :"none"}}>
         Loading......
         </div>)
    )}

     const performRedirect =()=>{
        if(getaRedirect){
            if(user && user.role===1){
            return <Navigate to="/admin/dashboard" />
            }
        }

    }


    const myCategoryForm = () =>{
        return(
            <Form>
      <Form.Group className="mb-4" >
        <Form.Label >Enter the Update category</Form.Label>
        <Form.Control type='text' className='p-3 my-2' onChange={handleChange}  placeholder="For example: Sofas" value={name} autoFocus required />
      </Form.Group>
      <Button className='shadow-lg   rounded'  variant="primary" onClick={onSubmit} type="submit">
        Update Category
      </Button>
    </Form>
        )
    }



  return (
    <Base title="Update a Category" description='Update a new category for new Furiture' className='container mt-mb-3 '>
        <Link className="btn btn-white bg-white  shadow-lg rounded mt-5 " to={`/admin/dashboard`}>
        <span className="mb-2 text-dark">Admin Home</span>
      </Link>
        <h2  className="text-center mb-0 py-4 text-white ">Update a Category</h2>
        <div className='row pl-0 p-3 m-4 ml-5 col-11 justify-content-center align-items-center  bg-white rounded'>
            <div className="div ml-0 col-md-8 offset-md-2">
                {successMessage()}
                {errorMessage()}
                {performRedirect()}
                {loadingMessage()}
                {myCategoryForm()}
                {/* {goBack()} */}
            </div>
        </div>
    </Base>
  )
}

export default UpdateCategory;
