// import React from 'react';
// import Base from '../core/Base'

// const Profile =()=> {
//   return (
//     <Base title='Profile Page'>
//     <h1>This is profile page</h1>
//     </Base>
//   )
// }

// export default Profile


import React, { useState, useEffect } from 'react';
import { isAuthenticated } from '../auth/helper';
import { getUser, updateUser } from '../user/helper/userHelper';

const Profile = () => {
  const { user, token } = isAuthenticated();
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: '',
    error: '',
    success: false,
  });

  const { name, email, password, error, success } = values;

  const preload = () => {
    getUser(user._id, token).then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({
          ...values,
          name: data.name,
          email: data.email,
        });
      }
    });
  };

  useEffect(() => {
    preload();
  }, []);

  const handleChange = (name) => (event) => {
    setValues({ ...values, error: false, [name]: event.target.value });
  };

  const onSubmit = (event) => {
    event.preventDefault();
    setValues({ ...values, error: false });
    updateUser(user._id, token, { name, email, password }).then((data) => {
      if (data.error) {
        setValues({ ...values, error: data.error, success: false });
      } else {
        setValues({
          ...values,
          name: data.name,
          email: data.email,
          success: true,
        });
      }
    });
  };

  const successMessage = () => (
    <div className="alert alert-success mt-3" style={{ display: success ? '' : 'none' }}>
      Profile updated successfully
    </div>
  );

  const errorMessage = () => (
    <div className="alert alert-danger mt-3" style={{ display: error ? '' : 'none' }}>
      {error}
    </div>
  );

  const profileForm = () => (
    <form>
      <div className="form-group">
        <label className="text-light">Name</label>
        <input
          className="form-control"
          type="text"
          onChange={handleChange('name')}
          value={name}
        />
      </div>
      <div className="form-group">
        <label className="text-light">Email</label>
        <input
          className="form-control"
          type="email"
          onChange={handleChange('email')}
          value={email}
        />
      </div>
      <div className="form-group">
        <label className="text-light">Password</label>
        <input
          className="form-control"
          type="password"
          onChange={handleChange('password')}
          value={password}
        />
      </div>
      <button className="btn btn-success" onClick={onSubmit}>
        Submit
      </button>
    </form>
  );

  return (
    <div className="container mt-5">
      <h2 className="text-center mb-4">Profile Update</h2>
      {successMessage()}
      {errorMessage()}
      {profileForm()}
    </div>
  );
};

export default Profile;
