import React,{useState,useEffect} from 'react';
import '../styles.css';
import Base from './Base';
import ProductCard from './Card';
import { loadCart } from './helper/cartHelper';
import StripeCheckout from './StripeCheckout';

// import { getProducts } from './helper/coreapicalls';


const Cart =() => {


    const [products, setProducts] = useState([]);
    const [reload, setReload] = useState(false)

    useEffect(() => {
        setProducts(loadCart())
    }, [reload]);

    const loadAllProducts = () =>{
        return(
            <div className='mx-auto' >
                <h2 className='text-white  text-center mb-3'>Your products in Cart</h2>
            <div className='m-2 p-2 ml-5'>                    
            {products.map((product,index)=>{
                    return(
                    <ProductCard 
                    key={index}
                    product={product} 
                    removeFromCart={true} 
                    addtoCart={false}
                    setReload={setReload}
                    reload={reload}

                    />

                )})}
</div>
            </div>
        )
    }


    const getFinalPrice =()=>{

        // return products.reduce((currentValue,nextValue)=>{
        //     return currentValue+nextValue.count*nextValue.price;
        // })

        let amount=0 

        products.map(product => {
            console.log(product);
            amount=amount+product.price;
        })
        return amount;
    }

    const loadCheckOut = () =>{
        return(
        <div >
                <h2 className='text-dark mb-3'>CheckOut Here</h2>
            </div>
    )}
return (
    <Base title="Cart Page" description="Ready to CheckOut" className='bg-white'>
    <div className="row text-center">
    <h2  className="text-center mb-2 py-4 text-dark ">Shopping Cart</h2>
        <div className='row'>
            <div className='col-lg-5 shadow-lg bg-dark mx-auto p-4 mb-4'>{loadAllProducts()}</div>
            <div className='col-lg-5 shadow-lg bg-white mx-auto p-4 mb-4'><StripeCheckout products={products} setReload={setReload} /></div>

        </div>
        </div>

    </Base>
);
}

export default Cart;
