import React,{useState} from "react";
import Base from '../core/Base';
import { Link } from "react-router-dom";
import { Col, Button, Row, Container, Card, Form } from 'react-bootstrap';

const Signup=()=> {

  //to store and set signup data

  const [values,setValues] = useState({
    name:"",
    email:"",
    password:"",
    error:"",
    success:false
  });
  //desctructing of values

  const {name,email,password,error,success} = values

  //handling changes on click

  const handleChange = name =>event =>{
    setValues({...values,error:false,[name]:event.target.name})
  }

  //submiting values
  const onSubmit =  event =>{
    event.preventDefault
  }

  //signup form design
    const signUpForm = () =>{
        return(
            <div>
                <Container>
        <Row className="vh-100 d-flex justify-content-center align-items-center">
          <Col md={8} lg={6} xs={12}>
            <Card className="px-4 shadow-lg shadow-inset">
              <Card.Body>
                <div className="mb-3 mt-md-4">
                  <h2 className="fw-bold mb-2 text-center text-dark text-uppercase ">
                    SignUp
                  </h2>
                  <div className="mb-3">
                    <Form>
                      <Form.Group className="mb-3" controlId="Name">
                        <Form.Label className="text-center text-dark">Name</Form.Label>
                        <Form.Control type="text" onChange={handleChange("name")} placeholder="Enter Name" />
                      </Form.Group>

                      <Form.Group className="mb-3" controlId="formBasicEmail">
                        <Form.Label className="text-center text-dark">
                          Email
                        </Form.Label>
                        <Form.Control type="email" onChange={handleChange("email")} placeholder="Enter email" />
                      </Form.Group>

                      <Form.Group
                        className="mb-3"
                        controlId="formBasicPassword"
                      >
                        <Form.Label className="text-center text-dark">Password</Form.Label>
                        <Form.Control type="password" onChange={handleChange("password")} placeholder="Password" />
                      </Form.Group>
                      <Form.Group
                        className="mb-3"
                        controlId="formBasicCheckbox"
                      ></Form.Group>
                      <div className="d-grid">
                        <Button className="btn-block" variant="primary" type="submit">
                          Create Account
                        </Button>
                      </div>
                    </Form>
                    <div className="mt-3">
                      <p className="mb-0 text-dark text-center">
                        Already have an account??{' '}
                        <Link className="text-primary fw-bold" to="/signin"> Sign In</Link>



                      </p>
                    </div>
                  </div>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
            </div>
        )
    }

  return (
    <Base title="SignUp" description="Register Here for shopping">
    {signUpForm()}
    </Base>

    )
}

export default Signup;
