import React,{useState} from "react";
import Base from '../core/Base';
import { Link,Redirect } from "react-router-dom";
import { Col, Button, Row, Container, Card, Form } from 'react-bootstrap';
import {signin,isAuthenticated,authenticate} from "../auth/helper"

const Signin=()=> {

  const [values,setValues] = useState({
    email:"",
    password:"",
    error:"",
    loading:"",
    didRedirect:false
  });

  const {email,password,error,loading,didRedirect} = values;

  //getting user from jwt in signup
  const {user} =isAuthenticated();

  //handling changes on click

  const handleChange = name =>event =>{
    setValues({...values,error:false,[name]:event.target.value})
  }

  //submiting values
  const onSubmit =  event =>{
    event.preventDefault()
    setValues({...values,error:false})
    signup({name,email,password})
    .then(data=>{
      if(data.error){
        setValues({...values,error:data.error,success:false})
      }else{
        setValues({...values,name:"",email:"",password:"",error:"",success:true})
      }
    })
    .catch(console.log("Error in SignUp"))
  }


  const signInForm = () =>{
    return(
        <div>
            <Container>
    <Row className="vh-100 d-flex justify-content-center align-items-center">
      <Col md={8} lg={6} xs={12}>
        <Card className="px-4 shadow-lg shadow-inset">
          <Card.Body>
            <div className="mb-3 mt-md-4">
              <h2 className="fw-bold mb-2 text-center text-dark text-uppercase ">
                SignIn
              </h2>
              <div className="mb-3">
                <Form>
                  <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label className="text-center text-dark">
                      Email
                    </Form.Label>
                    <Form.Control type="email" placeholder="Enter email" />
                  </Form.Group>

                  <Form.Group
                    className="mb-3"
                    controlId="formBasicPassword"
                  >
                    <Form.Label className="text-center text-dark">Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" />
                  </Form.Group>
                  <Form.Group
                    className="mb-3"
                    controlId="formBasicCheckbox"
                  ></Form.Group>
                  <div className="d-grid">
                    <Button className="btn-block" variant="primary" type="submit">
                      Create Account
                    </Button>
                  </div>
                </Form>
                <div className="mt-3">
                  <p className="mb-0 text-dark text-center">
                    If New User??{' '}
                    <Link className="text-primary fw-bold" to="/signup"> Sign Up</Link>



                  </p>
                </div>
              </div>
            </div>
          </Card.Body>
        </Card>
      </Col>
    </Row>
  </Container>
        </div>
    )
}

  return (
    <Base title="SignIn" description="Signin Here for shopping">
      {signInForm()}

    </Base>

    )
}

export default Signin;
