import React,{Fragment,useState} from 'react'
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Link, Navigate } from 'react-router-dom';
import './coreCss/Menu.css'
import { signout,isAuthenticated } from '../auth/helper';


const Menu =() => {
  lassName="vh-100 d-flex justify-content-center align-items-center"
  const [active, setActive] = useState('default');
  return (
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" className="vp-3">
      <Container>
      <Navbar.Brand href="/">FURS</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav" >
          <Nav activeKey={active}
        onSelect={(selectedKey) => setActive(selectedKey)}  className="me-auto navbar-nav ">
            <Nav.Link activeClassName="active"  >
              <Link eventKey="home" className="text-decoration-none text-white" to="/">
                Home
              </Link>
            </Nav.Link>
            <Nav.Link activeClassName="active">
              <Link className="text-decoration-none text-white" to="/cart">
                Cart
              </Link>
            </Nav.Link>

            {isAuthenticated() && isAuthenticated().user.role ===1 && (<Nav.Link>
              <Link className="text-decoration-none text-white" to="/admin/dashboard">
                Admin Dashboard
              </Link>
            </Nav.Link>)}
            {!isAuthenticated() && (<Fragment>
            <Nav.Link>

              <Link className="text-decoration-none text-white " to="/signin">
                SignIn
              </Link>
            </Nav.Link>
            <Nav.Link>

              <Link className="text-decoration-none text-white" to="/signup">
                SignUp
              </Link>
            </Nav.Link>
            </Fragment>)}
            {isAuthenticated() && (<Nav.Link>
              {' '}
              <Link className="text-decoration-none text-white" onClick={()=>{
                signout(()=>{
                  Navigate('/')
                })
              }} to="/signout">
              SignOut 
              </Link>
            </Nav.Link>)}

          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>

  )
}

// export default withRouter(Menu)
export default Menu
