import React,{useState,useEffect} from "react";
import ImageHelper from "./helper/imageHelper";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import '../styles.css'
import { Navigate } from "react-router";
import { addItemToCart, removeItemFromCart } from "./helper/cartHelper";


const ProductCard = ({ product,addtoCart=true,removeFromCart=false,reload }) => {

  const [redirect, setRedirect] = useState(false);
  const [count, setCount] = useState(product.count);



    const cartTitle = product ? product.name : "Default Name" ;
    const cartDescription = product ? product.description : "Default Description" ;
    const cartPrice = product ? product.price : "Default Price" ;

    const addToCart =()=>{
      addItemToCart(product,()=> setRedirect(true))
    }


    const getARedirect = redirect =>{
      if(redirect){
        return <Navigate to='/cart'></Navigate>
      }
    }


    const showAddToCart = addToCart =>{
        return (
            addtoCart && (<Button onClick={addToCart} className="shadow-md rounded px-5" variant="primary">Add to Cart</Button>)
        )

    }

    const showRemoveFromCart = (removeFromCart) =>{
        return (
            removeFromCart && (<Button onClick={()=>{
              removeItemFromCart(product._id)
              setReload(true)
            }} className="shadow-md rounded mt-2 px-4 " variant="danger">Remove from Cart</Button>) 
        )

    }

  return (
    <Card className="shadow-md bg-white ml-5 rounded d-flex  justify-content-around " style={{ width: '22rem'}}>
      {/* <Card.Img variant="top" src="holder.js/100px180" /> */}
     <ImageHelper product={product} />

      <Card.Body>
        {getARedirect(redirect)}
        <Card.Title className="text-dark">{cartTitle}</Card.Title>
        <Card.Text className="text-dark">
          {cartDescription}
        </Card.Text>
        <Button className="shadow-md rounded px-5 mb-2" variant="primary">${cartPrice}</Button>
        {showAddToCart(addToCart)}{showRemoveFromCart(removeFromCart)}


      </Card.Body>
    </Card>
    //Card
    // <div className="card text-white bg-dark border border-info ">
    //   <div className="card-header lead">A photo from pexels</div>
    //   <div className="card-body">
    //     <ImageHelper product={product} />
    //     <p className="lead bg-success font-weight-normal text-wrap">
    //       this photo looks great
    //     </p>
    //     <p className="btn btn-success rounded  btn-sm px-4">$ 5</p>
    //     <div className="row">
    //       <div className="col-12">
    //         <button
    //           onClick={() => {}}
    //           className="btn btn-block btn-outline-success mt-2 mb-2"
    //         >
    //           Add to Cart
    //         </button>
    //       </div>
    //       <div className="col-12">
    //         <button
    //           onClick={() => {}}
    //           className="btn btn-block btn-outline-danger mt-2 mb-2"
    //         >
    //           Remove from cart
    //         </button>
    //       </div>
    //     </div>
    //   </div>
    // </div>
  );
};

export default ProductCard;
