import React, {useState,useEffect} from 'react'
import Base from "../core/Base";
import { isAuthenticated } from '../auth/helper';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import {createCategory} from './helper/adminapicall'



const UpdateCategory = () => {

    const [name,setName] = useState("");
    const [error,setError] = useState(false);
    const [success,setSuccess] = useState(false);

    const {user,token} = isAuthenticated();

    const goBack = () =>(
        <div className="mt-5">
            <Link className='btn btn-danger btn-small mb-3 rounded' to="/admin/dashboard">Admin Home</Link>
        </div>
    )
    const preLoadCategories = ()=>{
        getAllCategories().then(data=>{
            if(data.error){
                setValues({...values,error:data.error})
            }
            else{
                setValues({
                    categories:data,formData:new FormData()
                })
            }
        })
    }

    const match = useMatch(
        "/admin/product/update/:productId"
      );


        useEffect(() => {
            preLoad(match.params.productId)
        }, []);


    const handleChange =(event)=>{

            setError("")
            setName(event.target.value)


    }

    const onSubmit =(event)=>{
        event.preventDefault();
        setError("");
        setSuccess(false);

        //backend request fired
        createCategory(user._id,token,{name}).then(data =>{
            if(data.error){
                setError(true)
            }else{
                setError("");
                setSuccess(true);
                setName("")
            }
        })
    }

    const successMessage = () =>{
        if(success){
            return <h3 className='text-success'>Category created successfully {name}</h3>
        }
    }

    const errorMessage = () =>{
        if(error){
            return <h3 className='text-danger'>Failed to created Category{name}</h3>
        }
    }

    const myCategoryForm = () =>{
        return(
            <Form>
      <Form.Group className="mb-3" >
        <Form.Label>Enter the category</Form.Label>
        <Form.Control type='text' className='my-3' onChange={handleChange}  placeholder="For example: Sofas" value={name} autoFocus required />
      </Form.Group>
      <Button variant="primary" onClick={onSubmit} type="submit">
        Submit
      </Button>
    </Form>
        )
    }



  return (
    <Base title="Create a Category" description='Add a new category for new Furiture' className='container bg-danger p-4 mt-mb-3 '>
        <div className='row bg-white rounded'>
            <div className="div col-md-8 offset-md-2">
                {successMessage()}
                {errorMessage()}
                {myCategoryForm()}
                {goBack()}
            </div>
        </div>
    </Base>
  )
}

export default UpdateCategory
