import { useParams } from 'react-router-dom';
import React,{useState,useEffect} from 'react'
import Base from '../core/Base';
import { Link,Navigate } from 'react-router-dom';
import { isAuthenticated } from '../auth/helper/index';
import {updateProduct,getProduct, getAllProducts} from '../admin/helper/adminapicall'






const UpdateProduct=(match)=> {




    const {user,token} = isAuthenticated();

    const [values,setValues] = useState({
        name:"",
        description:"",
        price:"",
        stock:"",
        photo:"",
        categories:[],
        category:"",
        loading:false,
        error:"",
        updatedProduct:"",
        getaRedirect:false,
        formData:""

    })

    const {name,description,price,stock,categories,category,loading,error,updatedProduct,getaRedirect,formData} = values;

    const preLoad = (productId) =>{
        getProduct(productId).then(data =>{

            // console.log(data);
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                setValues({
                    ...values,
                    name:data.name,
                    description:data.description,
                    price:data.price,
                    category:data.category,
                    stock:data.stock,
                    formData:new FormData()});
                console.log("CATE:" ,categories);
            }
        })
    }



    useEffect(() => {
        preLoad(match.params.product.d)
    }, []);

    const handleChange =name=> event =>{
        const value = name ==="photo" ? event.target.files[0] : event.target.value;
        formData.set(name,value);
        setValues({...values,[name]:value})

    }
    const onSubmit = (event) =>{
        event.preventDefault();
        setValues({...values,error:"",loading:true})
        updateProduct(user._id,token,formData)
        .then(data =>{
            if(data.error){
                setValues({...values,error:data.error})
            }else{
                setValues({...values,name:"",price:"",stock:"",description:"",photo:"",loading:false,updatedProduct:data.name})
            }
        })

}

    const successMessage =() =>(
        <div className="div alert alert-success mt-3" style={{display : updatedProduct ? "" : "none"}}>
            <h4>{updatedProduct} created Successfully</h4>
        </div>
    )

    const errorMessage =() =>(
        <div className="div alert alert-danger mt-3" style={{display : error ? "" : "none"}}>
            <h4>Not able to create {updatedProduct}</h4>
        </div>
    )

    const loadingMessage =()=>{
        return(
        loading && (
        <div className=" issue alert alert-info" style={{display: loading ? "" :"none"}}>
        Loading......
        </div>)
    )}

    const performRedirect =()=>{
        if(getaRedirect){
            if(user && user.role===1){
            return <Navigate to="/admin/dashboard" />
            }else{
            return <Navigate to="/user/dashboard" />
            }
        }

    }


    const createUpdateProductForm = () => (
        <form >
        <span>Post photo</span>
        <div className="form-group">
            <label className="btn btn-block btn-success">
            <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
            />
            </label>
        </div>
        <div className="form-group">
            <input
            onChange={handleChange("name")}
            name="photo"
            className="form-control"
            placeholder="Name"
            value={name}
            />
        </div>
        <div className="form-group">
            <textarea
            onChange={handleChange("description")}
            name="photo"
            className="form-control"
            placeholder="Description"
            value={description}
            />
        </div>
        <div className="form-group">
            <input
            onChange={handleChange("price")}
            type="number"
            className="form-control"
            placeholder="Price"
            value={price}
            />
        </div>
        <div className="form-group">
            <select
            onChange={handleChange("category")}
            className="form-control"
            placeholder="Category"
            >
            <option>Select</option>
                {categories && categories.map((category,index)=>{
                return(
                    <option key={index} value={category._id}>{category.name}</option>
                )
            })}

            </select>
        </div>
        <div className="form-group">
            <input
            onChange={handleChange("stock")}
            type="number"
            className="form-control"
            placeholder="Quantity"
            value={stock}
            />
        </div>

        <button type="submit" onClick={onSubmit} className="btn btn-outline-success mb-3">
            Create Product
        </button>
        </form>
    );

return (
    <Base title='Add a Product Here! ' description='Welcome to Product Creation Section' className='container bg-info p-4'>
        <Link to="/admin/dashboard" className='btn btn-md btn-dark mb-3'>Admin Home</Link>
    <div className="row bg-dark text-white rounded">
        <div className='col-md-8 offset-md-2'>
            {loadingMessage()}
            {performRedirect()}
            {successMessage()}
            {errorMessage()}
            {createUpdateProductForm()}
        </div>
    </div>
    </Base>
)
}


export default UpdateProduct;
