    const showStripeButton = () =>{
        return isAuthenticated() ? (
            <button  className="shadow-md rounded ml-3  px-3" variant="primary">Pay with Stripe</button>
        ):(
            <Link to="/signin"><button className="shadow-md rounded ml-3  px-3" variant="warning">SignIn</button></Link>
        )
    }


  return (
    <div>
    <h2 className='text-dark'>StripeCheckout loaded {getFinalPrice()}</h2>
    {showStripeButton()}
    </div>
  )
}

export default StripeCheckout;
